', '    \begin{tikzpicture}
', '      \draw [<->, thick] (1,1)--(5,0);
', '      \draw [fill] (2,0.75) circle [radius=0.05] node[below]{$J$};
', '      \draw [fill] (4,.25) circle [radius=0.05] node[above]{$K$};
', '      \node at (0,0) {(b)};
